mapper.select:
SELECT COUNT(*) FROM freeboards f JOIN users u ON f.writer = u.id where f.club_id in (select cm.club_id from club_members cm where cm.user_id=#{userId})
and (f.title like concat('%', #{keyword}, '%') or u.nickname like concat('%', #{keyword}, '%'))
and f.club_id = #{clubId}

SELECT f.id AS id, f.title AS title, f.writer AS writer, f.created_at AS createdAt, f.view_count AS viewCount, u.nickname AS writerNickname, u.username AS writerUsername, c.club_name AS clubName FROM freeboards f JOIN users u ON f.writer = u.id JOIN clubs c ON f.club_id = c.id WHERE f.club_id in (select cm.club_id from club_members cm where cm.user_id=#{userId})
and (f.title like concat('%', #{keyword}, '%') or u.nickname like concat('%', #{keyword}, '%'))
and f.club_id = #{clubId}
ORDER BY f.id DESC LIMIT #{rowCnt} OFFSET #{beginRowNo}

mapper.update:
UPDATE freeboards
SET view_count = view_count + 1
WHERE id = #{id}
