mapper.select:
select count(*) from galleries where club_id in (select club_id from club_members where user_id=#{id})
and (title like concat('%', #{keyword}, '%') or writer in (select id from users where nickname like concat('%', #{keyword}, '%')))
and club_id = #{clubId}

select id as ID, title as TITLE, content as CONTENT, writer as WRITER, created_at as CREATED_AT, view_count as VIEW_COUNT from galleries where club_id in (select club_id from club_members where user_id=#{id})
and (title like concat('%', #{keyword}, '%') or writer in (select id from users where nickname like concat('%', #{keyword}, '%')))
and club_id = #{clubId}
order by id desc limit #{rowCnt} offset #{beginRowNo}
